mapper.select:
	SELECT * FROM (
	    SELECT m.*, ROWNUM AS rnum FROM (
	        SELECT * FROM member


                    ${requestData.column} LIKE '%' || #{requestData.keyword} || '%'


	        ORDER BY id DESC
	    ) m
	) 
	WHERE rnum BETWEEN #{start} AND #{end}

mapper.update:
UPDATE member
SET status='inactive',
	inactive_date=SYSDATE,
	reactive_date=SYSDATE + #{banTime}
WHERE id=#{id}
