mapper.select:
select * from
(
select inner_query.*,ROWNUM as rn
	from(
		select ml.*,s.main_image1,s.store_name
		from member_like ml join store s
		on ml.store_id = s.id
		where ml.member_id = #{pageRequest.memberId}
		order by created_at desc
	) inner_query
)
where rn between #{startRow} and #{endRow}

select count(*) from
member_like
where member_id = #{id}

select * from
(
	select inner_query.*,ROWNUM as rn
	from(
		select r.*,s.main_image1,s.store_name
		from review r join store s
		on r.store_id = s.id
		where r.member_id = #{pageRequest.memberId}
		order by created_at desc
	) inner_query
)
where rn between #{startRow} and #{endRow}

select count(*) from
review
where member_id = #{id}

mapper.update:
update
member
set
introduction = #{introduction}
where nickname = #{nickname}

update
member
set
passwd = #{passwd}
where id = #{id}

update
member
set
profile_image = #{profileImage}
where id = #{id}
