mapper.select:
select * from (
	select notice.* , ROWNUM AS rn 
	from notice 
	where ROWNUM <= #{endRow}   
	)
 where rn >= #{startRow}

select * from (
	select event.* , ROWNUM AS rn 
	from event 
	where status = #{status}



		and category = #{category}

	and ROWNUM <= #{endRow}   
	)
 	where rn >= #{startRow}
