mapper.select:
SELECT * FROM (
    SELECT m.*, ROWNUM AS rnum FROM (
        SELECT * FROM notice 
        ORDER BY id DESC
    ) m
)
WHERE rnum BETWEEN #{start} AND #{end}

select * from (
	select notice.* , ROWNUM AS rn 
	from notice 
	where ROWNUM < #{endRow}   
	and category = #{category} -- 추가 조건
	)
 where rn >= #{startRow}

select count(*) from
notice
where category = #{category}

SELECT * FROM (
    SELECT e.*, ROWNUM AS rnum FROM (
        SELECT * FROM event 
        ORDER BY id DESC
    ) e
) 
WHERE rnum BETWEEN #{start} AND #{end}
