mapper.select:
SELECT * FROM (
    SELECT m.*, ROWNUM AS rnum FROM (
        SELECT * FROM store 
        ORDER BY id DESC
    ) m
) 
WHERE rnum BETWEEN #{start} AND #{end}

SELECT * FROM (
    SELECT m.*, ROWNUM AS rnum FROM (
        SELECT * FROM store 
        WHERE enroll_status='enrolled'
        ORDER BY id DESC
    ) m
) 
WHERE rnum BETWEEN #{start} AND #{end}
